function [TeX] = tex_cell2TeXsubsubsection(varargin)
  # -- Function: [TeX] = tex_cell2TeXsubsubsection(Title, [description, [,figure_ID [, table]]])
  #    this function expects at least one string to be used as the subsubsection title,
  #    and accepts:
  #      (optionally) another string, expected to be descriptive text
  #      (optionally) a file name of a graphic in the directory pwd/graphics (ie generated by printfigure)
  #      (optionally) a cell array of table data, which can contain mixed numbers and strings
  #

  Title = description = figure_ID = table = footnote = [];
  
	if (nargin < 1)
		error("need at least a string of title text ");
	else
    count = 1;
    Title = [varargin{count}];  count++; 
    idx = regexp(Title, '[%_$]');  Title(idx) = '_';
    Title = strrep(Title, '_', '\_');
    
    if (nargin >= count)
      description = [varargin{count}]; count++; 
      
      if (nargin >= count)
        figure_ID = [varargin{count}];count++; 
        if not(isempty(figure_ID)) && (length(figure_ID) > 0)
          idx = regexp(figure_ID, '[%_$]');  figure_ID(idx) = '_';
          figure_ID = strrep(figure_ID, '_', '\_');
        end
        
        if (nargin >= count)
          table = [varargin{count}];  count++;
          if (nargin >= count)
            footnote = [varargin{count}];
          end
        end
      end
    end
  end

 TeX = [  ...
    "\r\n" '%' ...
    "\r\n" '% auto-generated TeX  --------------------' ...
    "\r\n" '%' ...
    "\r\n" '\newpage \rule{0.9\columnwidth}{1pt}' ...
    "\r\n" '\subsubsection{ '  Title '}' ...
    "\r\n" '% text, e.g. a plot description:' ...
    "\r\n\t"  description ...
    ];
    
  if isempty(figure_ID)
  else
    TeX = [ TeX  ...
     "\r\n" '% a plot:' ...
    "\r\n\t" '\begin{center}' ...
    "\r\n\t" '\begin{figure}[!ht]' ...
    "\r\n\t" '\includegraphics[width=\columnwidth]{graphics/' figure_ID '.png}' ...
    "\r\n\t" '\end{figure}' ...
    "\r\n\t" '\end{center}' ...
    ];
  end
  
  TeX = [ TeX ...
    "\r\n" '% text, e.g. a table:' ...
    "\r\n\t" table ...
    "\r\n" '% more text, e.g. a footnote:' ...
    "\r\n\t"  footnote ...
    "\r\n" '\stepcounter{subsubsection}' ...
    ];
end
